import React from 'react'
import './affichePhoto.css'
import { useEffect } from 'react'
import { useState } from 'react';
import axios from 'axios';
import { useContext } from 'react';
import { AuthContext } from '../../Context/AuthContext';

export default function AffichePhoto(props) {
//  const [srcImg, setsrcImg] = useState(props.allImg.length===0 ? "":props.allImg[0].image );
const [srcImg, setsrcImg] = useState("");
 const [file, setfile] = useState("");
 const {token,settoken}  = useContext(AuthContext);
 const [imgDownload, setimgDownload] = useState(false);

 function ImgCLick(e,id){
    const allImgs = document.querySelectorAll('.imgChoix');
    allImgs.forEach(element => {
        element.style.border = 'none';
    });
    const newTab = props.allImg.filter((el)=>el._id===id);
    console.log(newTab[0].image);
    setsrcImg(newTab[0].image);
    e.target.style.border ='2px solid red';
 }

 async function handleimg(){
    const config = {
      headers: {
        Authorization: `Bearer ${token}`,
      },
    };
  
    const formdata = new FormData();
    formdata.append('file',file);
  
    await axios.post("/photo/upload",formdata,config)
      .then((res)=>console.log(res))
      .catch((err)=>console.log(err));

    await  axios.get("/photo/getImage",config)
      .then((res)=>{
        props.setallImg(res.data);
        setsrcImg(res.data[res.data.length-1].image);
        const allImgs = document.querySelectorAll('.imgChoix');
        console.log(allImgs)
        allImgs.forEach(element => {
            element.style.border = 'none';
        });
        setimgDownload(true);
      })
      .catch((err)=>console.log(err));
  }

  async function changerImg (src){
    const config = {
      headers: {
        Authorization: `Bearer ${token}`,
      },
    };

    const img = {src};
    
    await  axios.post("/photo/preferenceImg",img,config)
    .then((res)=>{
      console.log(res);
      props.setimgPref(img);
      props.setbolAffichePhoto(!props.bolAffichePhoto);
    })
    .catch((err)=>console.log(err));
  }

  function affichePremiereImg(){
   
    if(props.allImg.length===0){
      return (
        " "
      )
    }
    if(props.bolAffichePhoto === true ){
      console.log(props.imgPref);
      return(
        <img src={`http://localhost:5000`+props.imgPref} alt="" />
      )
    }
    else{
      return(
        <img src={`http://localhost:5000`+srcImg} alt="" />
      )
    }
  }

  useEffect(() => {
    if(props.bolAffichePhoto===true){
      setimgDownload(false);
    }
  }, [props.bolAffichePhoto])


  return (
    <div className='containerAffichePhoto'>
        <p>
          {affichePremiereImg()}
          <input type="file" onChange={e=>setfile(e.target.files[0])} />
          <button  onClick={handleimg}>telecharger</button>
          <button  onClick={()=>changerImg(srcImg)}>termin√©</button>
        </p>
        <p>
            {props.allImg.length===0 ? "pas dimage" :  props.allImg.map((element,index)=>{
              if(index === props.allImg.length-1 && imgDownload===true){
                  return(
                    <img style={{border:"solid red 2px"}} className='imgChoix' onClick={(e)=>ImgCLick(e,element._id)} key={element._id}  src={`http://localhost:5000`+element.image} alt="" />
                  )
              }
                return(
                    <img className='imgChoix' onClick={(e)=>ImgCLick(e,element._id)} key={element._id}  src={`http://localhost:5000`+element.image} alt="" />
                )
            })}
        </p>
    </div>
  )
}
