import React from "react";
import "./signIn.css";
import { useEffect } from "react";
import { useRef } from "react";
import croix from "../../photo/croix.png";
import { useContext } from "react";
import { useNavigate } from "react-router-dom";
import { AuthContext } from "../../Context/AuthContext";
import { useState } from "react";
import axios from "axios";

export default function SignUp(props) {
  const navigate = useNavigate();
  const ContainerSignIn = useRef();
  const [mail, setmail] = useState("");
  const [password, setpassword] = useState("");
  const { token, settoken } = useContext(AuthContext);
  const [erreurMsg, seterreurMsg] = useState("");

  const config = {
    headers: {
      Authorization: `Bearer ${token}`,
    },
  };

  const onclickCroix = () => {
    props.setSignIn(false);
  };

  useEffect(() => {
    if (props.signIn === true) {
      ContainerSignIn.current.style.display = "flex";
    }
    if (props.signIn === false) {
      ContainerSignIn.current.style.display = "none";
    }
  }, [props.signIn, props.setSignIn]);

  const valideForm = async (e) => {
    e.preventDefault();
    if (mail === "") {
      return seterreurMsg("Veuillez indiquer le mail.");
    }
    if (password === "") {
      return seterreurMsg("Veuillez indiquer le password.");
    }
    const connection = { email: mail, password };

    await axios
      .post("/user/login", connection)
      .then((res) => {
        setmail("");
        setpassword("");
        localStorage.setItem("token", res.data);
        settoken(res.data);
        props.settest(true);
      })
      .catch((err) => seterreurMsg("bonjour"));
  };

  useEffect(() => {
    if (token) {
      axios
        .get("/photo/getImage", config)
        .then((res) => {
          props.setallImg(res.data);
          props.setSignIn(false);
          navigate("/");
        })
        .catch((err) => console.log(err));

      axios
        .get("/photo/prefImage", config)
        .then((res) => props.setimgPref(res.data[0].image))
        .catch((err) => console.log(err));
    } else {
      return;
    }
  }, [token]);

  return (
    <form
      ref={ContainerSignIn}
      className="opacity-1 fixed top-2/4 left-2/4 w-418 h-500 bg-gradient-to-r from-vertFoncer from-0%  to-vertClair to-100% z-50 -translate-y-2/4 -translate-x-2/4 rounded-3xl p-9 flex flex-col items-center justify-between"
    >
      <div className="flex items-center justify-between w-full text-blanc">
        <h2>Se connecter</h2>
        <button
          className="size-7 absolute right-5 rounded-s-full border-none bg-noir transition-all duration-200 ease-in-out"
          onClick={onclickCroix}
        >
          <img src={croix} alt="" />
        </button>
      </div>
      <div className="SignInMailMdp">
        <label htmlFor="">Email</label>
        <input
          value={mail}
          onChange={(e) => {
            setmail(e.target.value);
            seterreurMsg("");
          }}
          placeholder="texte@exemple.com"
          type="text"
        />
        <label htmlFor="">Password</label>
        <input
          value={password}
          onChange={(e) => {
            setpassword(e.target.value);
            seterreurMsg("");
          }}
          placeholder="*******"
          type="text"
        />
      </div>
      <div className="SignInLogIn">
        <button onClick={valideForm}>Connexion</button>
      </div>
      <p className="erorMsgSignIn"></p>
      <div className="SignInCreateAccount">
        <p>Vous n'avez toujours pas de compte? Cr√©er</p>
      </div>
    </form>
  );
}
